mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

mapper.select:
SELECT a.*
FROM article a
WHERE id = #{id}

SELECT *
FROM article
WHERE boardId = #{boardId}
ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM article
WHERE boardId = #{boardId}
